<?php//echo form_radio('tag',array(1=>'显示',0=>'隐藏',2=>'很好'),1);function form_radio($name,$array,$checked='-1',$id_prefix='x_'){   foreach($array as $key=>$value){		if($checked==$key){			$c="checked='checked'";		}else{			$c='';		}		$string.=<<<st\n\r<label for='{$id_prefix}{$key}'><input type="radio" name="{$name}" value="{$key}" {$c} id='{$id_prefix}{$key}' />{$value}</label>st;   }   return $string."\r\n";}//echo form_checkbox('mycheckbox',array(0=>'音乐',1=>'体育',2=>'政治'),array(1,2));function form_checkbox($name,$array,$checked=array(),$id_prefix='y_'){	foreach($array as $key=>$value){		if(in_array($key,$checked)){			$c="checked='checked'";		}else{			$c='';		}		$string.=<<<st\n\r<label for='{$id_prefix}{$key}'><input type="checkbox" name="{$name}" value="{$key}" {$c} id='{$id_prefix}{$key}' />{$value}</label>st;   }   return $string."\r\n";}//echo form_select(array(0=>'第0个',1=>'第一个',2=>'第二个'),2);function form_select($array,$selected='-1'){	foreach($array as $key=>$value){		if($selected==$key){			$s="selected='selected'";		}else{			$s='';		}		$string.=<<<st\n\r<option value='{$key}' {$s} >{$value}</option>st;	}	return $string."\r\n";}